import React, { useState, useEffect } from "react";
import axios from "axios";

function PatientPage() {
  const [appointments, setAppointments] = useState([]);
  const [medicalRecord, setMedicalRecord] = useState(null);

  useEffect(() => {
    // Récupérer les rendez-vous (à implémenter via le backend plus tard)
    setAppointments(["Rendez-vous le 2025-06-01 avec Dr. Smith"]);
    // Récupérer le dossier médical (exemple statique pour l'instant)
    setMedicalRecord({
      prescriptions: ["Paracétamol 500mg - 2 fois/jour"],
      reports: ["Compte-rendu du 2025-05-20 : Tout va bien"],
    });
  }, []);

  const handleContactDoctor = () => {
    alert("Message envoyé au médecin !");
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>Portail Patient</h1>
      <h2>Dossier Médical</h2>
      {medicalRecord ? (
        <div>
          <h3>Prescriptions</h3>
          <ul>
            {medicalRecord.prescriptions.map((prescription, index) => (
              <li key={index}>{prescription}</li>
            ))}
          </ul>
          <h3>Comptes-rendus</h3>
          <ul>
            {medicalRecord.reports.map((report, index) => (
              <li key={index}>{report}</li>
            ))}
          </ul>
        </div>
      ) : (
        <p>Chargement du dossier médical...</p>
      )}
      <h2>Rendez-vous</h2>
      <ul>
        {appointments.map((appointment, index) => (
          <li key={index}>{appointment}</li>
        ))}
      </ul>
      <button onClick={handleContactDoctor}>Contacter mon médecin</button>
    </div>
  );
}

export default PatientPage;
